import { UiButton } from '../../public-api'

export enum UiButtonActions {
  COMMENT = 'COMMENT',
  LIKE = 'LIKE',
}

export enum UiButtonIcons {
  COMMENT = 'fa fa-fw fa-comment',
  LIKE = 'fa fa-fw fa-heart',
}

export class ButtonHelper {
  static button(config: UiButton): UiButton {
    return { ...config }
  }
  static comment(config: UiButton): UiButton {
    return ButtonHelper.button({
      icon: config.icon || config.iconAfter ? null : UiButtonIcons.COMMENT,
      action: config.action || config.path ? null : UiButtonActions.COMMENT,
      ...config,
    })
  }

  static like(config: UiButton): UiButton {
    return ButtonHelper.button({
      icon: config.icon || config.iconAfter ? null : UiButtonIcons.LIKE,
      action: config.action || config.path ? null : UiButtonActions.LIKE,
      ...config,
    })
  }
}

import { FormlyFieldConfig, FormlyTemplateOptions } from '@ngx-formly/core'

export class FormHelper implements FormlyFieldConfig {
  public static map(f: string) {
    const map = {
      input: FormHelper.input,
      email: FormHelper.email,
      password: FormHelper.password,
      number: FormHelper.number,
      checkbox: FormHelper.checkbox,
      radio: FormHelper.radio,
      select: FormHelper.select,
      textarea: FormHelper.textarea,
      group: FormHelper.group,
      template: FormHelper.template,
      date: FormHelper.date,
      time: FormHelper.time,
    }
    return map[f] ? map[f] : FormHelper.input
  }

  public static field(
    type: string,
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return {
      type,
      key,
      templateOptions,
      ...options,
    }
  }

  public static input(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('input', key, templateOptions, options)
  }

  public static email(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    const defaults = {
      type: 'email',
      label: 'Email',
    }
    const defaultOptions = { validators: { validation: ['email'] } }

    return FormHelper.input(
      key,
      { ...templateOptions, ...defaults },
      { ...options, ...defaultOptions },
    )
  }

  public static password(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    const defaults = {
      label: 'Password',
      type: 'password',
      minLength: 8,
      required: true,
    }

    return FormHelper.input(key, { ...templateOptions, ...defaults }, options)
  }

  public static number(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.input(key, { ...templateOptions, type: 'number' }, { ...options })
  }

  public static checkbox(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('checkbox', key, templateOptions, options)
  }

  public static radio(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('radio', key, templateOptions, options)
  }

  public static select(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('select', key, templateOptions, options)
  }

  public static typeahead(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('typeahead', key, templateOptions, options)
  }

  public static textarea(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    const defaultTemplateOptions = { rows: 5 }

    return FormHelper.field(
      'textarea',
      key,
      { ...defaultTemplateOptions, ...templateOptions },
      options,
    )
  }

  public static group(
    fieldGroupClassName: string,
    fieldGroup: FormlyFieldConfig[],
  ): FormlyFieldConfig {
    return { fieldGroupClassName, fieldGroup }
  }

  public static template(template: string): FormlyFieldConfig {
    return { type: 'formly-template', template }
  }

  public static date(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.input(key, { ...templateOptions, type: 'date' }, { ...options })
  }

  public static time(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.input(key, { ...templateOptions, type: 'time' }, { ...options })
  }

  public static datetime(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('datetime', key, { ...templateOptions }, { ...options })
  }

  public static rating(
    key: string,
    templateOptions?: FormlyTemplateOptions,
    options?: any,
  ): FormlyFieldConfig {
    return FormHelper.field('rating', key, { ...templateOptions }, { ...options })
  }
}

