  selector: 'ui-tabs',
  template: `
    <ul class="nav nav-pills nav-fill">
      <li class="nav-item" *ngFor="let tab of tabs">
        <a
          class="nav-link py-3"
          routerLinkActive="active"
          [routerLink]="tab.path"
          [queryParams]="tab.queryParams"
          >{{ tab.label }}</a
        >
      </li>
    </ul>
  `,
